{# Dictionaries of VLANS per Site #}
{% set MgmtAceEntries = [
  {'entry':'10','rule':'permit','range':'10.10.0.100','mask':'0.0.0.0'},
  {'entry':'20','rule':'permit','range':'10.10.0.200','mask':'0.0.0.0'},
  {'entry':'30','rule':'permit','range':'10.10.0.0','mask':'0.0.0.255'},
  {'entry':'40','rule':'permit','range':'10.20.0.0','mask':'0.0.0.255'}
  ]%}
{% set VtyAccessAceEntries = [
  {'entry':'10','rule':'permit','range':'10.10.0.100','mask':'0.0.0.0'},
  {'entry':'20','rule':'permit','range':'10.10.0.200','mask':'0.0.0.0'},
  {'entry':'30','rule':'permit','range':'10.10.0.0','mask':'0.0.0.255'},
  {'entry':'40','rule':'permit','range':'10.20.0.0','mask':'0.0.0.255'},
  {'entry':'50','rule':'permit','range':'10.30.0.0','mask':'0.0.0.255'},
  {'entry':'60','rule':'permit','range':'10.40.0.0','mask':'0.0.0.255'}
  ]%}
{% set AclDefaultEntries = [
  {'entry':'10','rule':'permit','range':'10.10.0.100','mask':'0.0.0.0'},
  {'entry':'20','rule':'permit','range':'10.10.0.200','mask':'0.0.0.0'},
  {'entry':'30','rule':'permit','range':'10.10.0.0','mask':'0.0.0.255'}
  ]%}

{# ACLs Standard Macro - Pragmatically Build Standard ACL from Object #}
{% macro StandardAcl(name,lists) %}
  ip access-list standard {{ name }}
  {% for list in lists %}
    {{ list['entry'] }} {{ list['rule'] }} {{ list['range'] }} {{ list['mask'] }}
  {% endfor %}
{% endmacro %}

{# ACLs Extended Macro - Pragmatically Build Extended ACL from Object #}
{% macro ExtendedAcl(name,lists) %}
  ip access-list extended {{ name}}
  {% for list in lists %}
    {{ list.entry }} {{ list.rule }} {{ list.srange }} {{ list.smask }} {{ list.sport }} {{ list.drange }} {{ list.dmask }} {{ list.dport }}
  {% endfor %}
{% endmacro %}

{# SNMP ACL #}
{% macro StandardSnmpLineAcl() %}
 access-list 10 permit 10.10.0.100
 access-list 10 permit 10.20.0.200
 access-list 10 permit 10.10.0.0 0.0.0.255
 access-list 10 permit 10.20.0.0 0.0.0.255
{% endmacro %}
!
{# SNMP ACL - Named ACL #}
{% macro StandardSnmpAcl() %}
ip access-list standard SNMP
 10 permit 10.10.0.100
 20 permit 10.20.0.200
 30 permit 10.10.0.0 0.0.0.255
 40 permit 10.20.0.0 0.0.0.255
{% endmacro %}
!
{# VTY ACL #}
{% macro StandardVtyLineAcl() %}
 access-list 20 permit 10.10.0.100
 access-list 20 permit 10.20.0.200
 access-list 20 permit 10.10.0.0 0.0.0.255
 access-list 20 permit 10.20.0.0 0.0.0.255
{% endmacro %}
!
{# VTY ACL - Named ACL #}
{% macro StandardVtyAcl() %}
ip access-list standard VTY
 10 permit 10.10.0.100
 20 permit 10.20.0.200
 30 permit 10.10.0.0 0.0.0.255
 40 permit 10.20.0.0 0.0.0.255
{% endmacro %}
!
{# The below example uses the macros above #}
{{ StandardSnmpLineAcl() }}
{{ StandardSnmpAcl() }}
{{ StandardVtyLineAcl() }}
{{ StandardVtyAcl() }}

{# The below example uses the objects above and macros - Example in comments below #}
{# ip access-list standard ACL-DEFAULT #}
{#  10 permit any #}
{{ StandardAcl("ACL-DEFAULT",AclDefaultEntries) }}

